import React, { useMemo, useState } from 'react';
import { Search, Package, DollarSign, AlertTriangle, Shapes, Menu } from 'lucide-react';

// --- Small helper component for stat cards ---
function StatCard({ title, value, icon: Icon, subtitle }) {
  return (
    <div className="rounded-2xl border border-gray-200 bg-white p-4 shadow-sm sm:p-5">
      <div className="flex items-center justify-between">
        <div className="space-y-1">
          <p className="text-sm text-gray-500">{title}</p>
          <p className="text-2xl font-semibold tracking-tight sm:text-3xl">{value}</p>
          {subtitle ? <p className="text-xs text-gray-400">{subtitle}</p> : null}
        </div>
        <div className="rounded-2xl border p-2">
          <Icon className="h-5 w-5" aria-hidden />
        </div>
      </div>
    </div>
  );
}

// --- Avatar (initials) ---
function Avatar({ name = 'Admin User' }) {
  const initials = useMemo(
    () =>
      name
        .split(' ')
        .map((n) => n[0])
        .slice(0, 2)
        .join('')
        .toUpperCase(),
    [name],
  );

  return (
    <div
      className="inline-flex h-9 w-9 items-center justify-center rounded-full bg-gradient-to-br from-gray-800 to-gray-600 text-sm font-semibold text-white shadow"
      aria-label={`${name} avatar`}
      title={name}
    >
      {initials}
    </div>
  );
}

// --- Main Dashboard ---
export default function ProductDashboard() {
  // In a real app these would come from your API or context
  const [query, setQuery] = useState('');

  const data = {
    totalProducts: 1280,
    totalRevenue: 482350.75, // USD for example
    lowStock: 23,
    categoriesCount: 12,
  };

  const formattedRevenue = useMemo(
    () =>
      new Intl.NumberFormat(undefined, {
        style: 'currency',
        currency: 'USD',
        maximumFractionDigits: 0,
      }).format(data.totalRevenue),
    [data.totalRevenue],
  );

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="sticky top-0 z-30 border-b border-gray-200 bg-white/80 backdrop-blur">
        <div className="mx-auto flex max-w-7xl items-center justify-between gap-3 px-4 py-3 sm:py-4">
          {/* Left: Brand + Nav trigger (mobile) */}
          <div className="flex items-center gap-2">
            <button
              className="inline-flex h-9 w-9 items-center justify-center rounded-xl border border-gray-200 bg-white sm:hidden"
              aria-label="Open navigation"
            >
              <Menu className="h-5 w-5" />
            </button>
            <div className="font-semibold tracking-tight">Admin Dashboard</div>
          </div>

          {/* Center: Search */}
          <div className="relative hidden min-w-0 flex-1 sm:block">
            <Search className="pointer-events-none absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 opacity-60" />
            <input
              type="search"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search products, categories, orders…"
              className="w-full rounded-xl border border-gray-200 bg-gray-50 py-2 pl-9 pr-3 text-sm outline-none ring-0 placeholder:text-gray-400 focus:bg-white focus:ring-2 focus:ring-gray-200"
            />
          </div>

          {/* Right: Actions */}
          <div className="flex items-center gap-3">
            {/* Compact search on mobile */}
            <div className="relative sm:hidden">
              <Search className="pointer-events-none absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 opacity-60" />
              <input
                type="search"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
                placeholder="Search…"
                className="w-36 rounded-xl border border-gray-200 bg-gray-50 py-2 pl-9 pr-3 text-sm outline-none ring-0 placeholder:text-gray-400 focus:bg-white focus:ring-2 focus:ring-gray-200 sm:hidden"
              />
            </div>

            <Avatar name="Surya Kant Sharma" />
          </div>
        </div>
      </header>

      {/* Content */}
      <main className="mx-auto max-w-7xl px-4 pb-16 pt-6 sm:pt-8">
        {/* Stats Grid */}
        <section className="grid grid-cols-1 gap-3 sm:grid-cols-2 sm:gap-4 lg:grid-cols-4">
          <StatCard
            title="Total Products"
            value={data.totalProducts.toLocaleString()}
            subtitle="All active SKUs"
            icon={Package}
          />
          <StatCard
            title="Total Revenue"
            value={formattedRevenue}
            subtitle="Last 12 months"
            icon={DollarSign}
          />
          <StatCard
            title="Low Stock Items"
            value={data.lowStock}
            subtitle="Below threshold"
            icon={AlertTriangle}
          />
          <StatCard
            title="Categories Count"
            value={data.categoriesCount}
            subtitle="Live categories"
            icon={Shapes}
          />
        </section>

        {/* Placeholder for additional responsive content */}
        <section className="mt-6 grid grid-cols-1 gap-4 lg:grid-cols-3">
          <div className="rounded-2xl border border-gray-200 bg-white p-4 shadow-sm lg:col-span-2">
            <h2 className="mb-3 text-base font-semibold">Recent Activity</h2>
            <ul className="space-y-3 text-sm text-gray-600">
              <li className="flex items-center justify-between">
                <span>Imported 250 new products</span>
                <span className="text-xs text-gray-400">2h ago</span>
              </li>
              <li className="flex items-center justify-between">
                <span>Updated category mapping</span>
                <span className="text-xs text-gray-400">5h ago</span>
              </li>
              <li className="flex items-center justify-between">
                <span>Low stock alert triggered</span>
                <span className="text-xs text-gray-400">Yesterday</span>
              </li>
            </ul>
          </div>
          <div className="rounded-2xl border border-gray-200 bg-white p-4 shadow-sm">
            <h2 className="mb-3 text-base font-semibold">Quick Actions</h2>
            <div className="grid grid-cols-2 gap-3">
              {['Add Product', 'Import CSV', 'Create Category', 'Inventory Audit'].map((label) => (
                <button
                  key={label}
                  className="rounded-xl border border-gray-200 bg-gray-50 px-3 py-2 text-sm hover:bg-white hover:shadow"
                >
                  {label}
                </button>
              ))}
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}
