import React, { useContext, useEffect, useMemo, useState } from 'react';
import { AppContext } from '../context/AppContext';
import StatCard from './StartCard';

function Avatar({ name = 'Admin User' }) {
  const initials = useMemo(
    () =>
      name
        .split(' ')
        .map((n) => n[0])
        .slice(0, 2)
        .join('')
        .toUpperCase(),
    [name],
  );

  return (
    <div
      className="inline-flex h-9 w-9 items-center justify-center rounded-full bg-gray-800 text-sm font-semibold text-white"
      title={name}
    >
      {initials}
    </div>
  );
}

export default function ProductDashboard() {
  const [data, setData] = useState({
    totalProducts: 1280,
    totalRevenue: 482350.75,
    lowStock: 23,
    categoriesCount: 12,
  });
  const { products } = useContext(AppContext);

  useEffect(() => {
    if (products.length) {
      let totalProducts = products.length;
      let totalRevenue = 0;
      let lowStock = 0;
      const categories = new Set();

      for (const p of products) {
        totalRevenue += p.price * p.stock;
        if (p.stock < 10) lowStock++;
        categories.add(p.category);
      }

      setData({
        totalProducts,
        totalRevenue,
        lowStock,
        categoriesCount: categories.size,
      });
    }
  }, [products]);

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="sticky top-0 z-30 border-b border-gray-200 bg-white">
        <div className="mx-auto flex max-w-7xl items-center justify-between gap-3 px-4 py-3 sm:py-4">
          <div className="font-semibold tracking-tight">Admin Dashboard</div>

          <img
            src={`https://picsum.photos/seed/product${Math.floor(Math.random() * 100)}/50/50`}
            alt="User Avatar"
            className="rounded-full border border-gray-300"
          />
        </div>
      </header>

      <main className="mx-auto max-w-7xl px-4 pb-16 pt-6 sm:pt-8">
        <section className="grid grid-cols-1 gap-3 sm:grid-cols-2 sm:gap-4 lg:grid-cols-4">
          <StatCard
            title="Total Products"
            value={data.totalProducts.toLocaleString()}
            subtitle="All products"
          />
          <StatCard
            title="Total Revenue"
            value={'₹' + data.totalRevenue.toLocaleString()}
            subtitle=""
          />
          <StatCard title="Low Stock Items" value={data.lowStock} subtitle="Below threshold" />
          <StatCard
            title="Categories Count"
            value={data.categoriesCount}
            subtitle="Live categories"
          />
        </section>
      </main>
    </div>
  );
}
