import React, { useContext } from 'react';
import { AppContext } from '../context/AppContext';

export default function CartSidebar() {
  const { cart, isCartOpen, setIsCartOpen, updateQty, removeItem } = useContext(AppContext);

  const total = cart.reduce((sum, item) => sum + item.price * item.qty, 0).toFixed(2);

  return (
    <>
      {isCartOpen && (
        <div
          className="fixed inset-0 bg-black bg-opacity-30 z-40"
          onClick={() => setIsCartOpen(false)}
        />
      )}

      <div
        className={`fixed top-0 right-0 w-80 h-full bg-white shadow-lg p-4 z-50 transform transition-transform duration-300 ${
          isCartOpen ? 'translate-x-0' : 'translate-x-full'
        }`}
      >
        <div className="flex justify-between items-center mb-4">
          <h2 className="text-lg font-bold">Your Cart</h2>
          <button onClick={() => setIsCartOpen(false)} className="text-gray-500">
            ✕
          </button>
        </div>

        {cart.length === 0 ? (
          <p className="text-gray-500">Your cart is empty</p>
        ) : (
          <div className="space-y-4 overflow-y-auto max-h-[70vh] pr-2">
            {cart.map((item) => (
              <div key={item.id} className="flex items-center justify-between border-b pb-2">
                <img src={item.image} alt={item.name} className="w-12 h-12 rounded" />
                <div className="flex-1 px-2">
                  <p className="font-medium">{item.name}</p>
                  <p className="text-sm text-gray-500">₹{item.price}</p>
                  <div className="flex items-center gap-2 mt-1">
                    <button
                      onClick={() => updateQty(item.id, Math.max(item.qty - 1, 1))}
                      className="px-2 bg-gray-200 rounded"
                    >
                      -
                    </button>
                    <span>{item.qty}</span>
                    <button
                      onClick={() => updateQty(item.id, item.qty + 1)}
                      className="px-2 bg-gray-200 rounded"
                    >
                      +
                    </button>
                  </div>
                </div>
                <button onClick={() => removeItem(item.id)} className="text-red-500 font-bold">
                  ✕
                </button>
              </div>
            ))}
          </div>
        )}

        {cart.length > 0 && (
          <div className="mt-4 border-t pt-4">
            <div className="flex justify-between font-bold">
              <span>Total:</span>
              <span>₹{total}</span>
            </div>
            <button className="w-full mt-3 bg-blue-500 text-white py-2 rounded hover:bg-blue-600 transition">
              Checkout
            </button>
          </div>
        )}
      </div>
    </>
  );
}
