import React, { useContext, useMemo, useState } from 'react';
import { AppContext } from '../context/AppContext';

function StatCard({ title, value, subtitle }) {
  return (
    <div className="rounded-2xl border border-gray-200 bg-white p-4 shadow-sm sm:p-5">
      <div className="flex items-center justify-between">
        <div className="space-y-1">
          <p className="text-sm text-gray-500">{title}</p>
          <p className="text-2xl font-semibold tracking-tight sm:text-3xl">{value}</p>
          {subtitle ? <p className="text-xs text-gray-400">{subtitle}</p> : null}
        </div>
      </div>
    </div>
  );
}

function Avatar({ name = 'Admin User' }) {
  const initials = useMemo(
    () =>
      name
        .split(' ')
        .map((n) => n[0])
        .slice(0, 2)
        .join('')
        .toUpperCase(),
    [name],
  );

  return (
    <div
      className="inline-flex h-9 w-9 items-center justify-center rounded-full bg-gray-800 text-sm font-semibold text-white"
      title={name}
    >
      {initials}
    </div>
  );
}

export default function ProductDashboard() {
    const [query, setQuery] = useState('');
    const [data, setData] = useState({
      totalProducts: 1280,
      totalRevenue: 482350.75,
      lowStock: 23,
      categoriesCount: 12,
    });
    const { products } = useContext(AppContext);


    useEffect(() => {
        if (products.length > 0) {
           setData()
        }
    }, [products])

//   const data = {
//     totalProducts: 1280,
//     totalRevenue: 482350.75,
//     lowStock: 23,
//     categoriesCount: 12,
//   };

  const formattedRevenue = useMemo(
    () =>
      new Intl.NumberFormat(undefined, {
        style: 'currency',
        currency: 'INR',
        maximumFractionDigits: 0,
      }).format(data.totalRevenue),
    [data.totalRevenue],
  );

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="sticky top-0 z-30 border-b border-gray-200 bg-white">
        <div className="mx-auto flex max-w-7xl items-center justify-between gap-3 px-4 py-3 sm:py-4">
          <div className="font-semibold tracking-tight">Admin Dashboard</div>

          <Avatar name="Surya Kant Sharma" />
        </div>
      </header>

      <main className="mx-auto max-w-7xl px-4 pb-16 pt-6 sm:pt-8">
        <section className="grid grid-cols-1 gap-3 sm:grid-cols-2 sm:gap-4 lg:grid-cols-4">
          <StatCard
            title="Total Products"
            value={data.totalProducts.toLocaleString()}
            subtitle="All active SKUs"
          />
          <StatCard title="Total Revenue" value={formattedRevenue} subtitle="Last 12 months" />
          <StatCard title="Low Stock Items" value={data.lowStock} subtitle="Below threshold" />
          <StatCard
            title="Categories Count"
            value={data.categoriesCount}
            subtitle="Live categories"
          />
        </section>
      </main>
    </div>
  );
}
