import React, { useContext, useState, useRef, useMemo, useCallback, useEffect } from 'react';
import { AppContext } from '../context/AppContext';
import { paginateData } from '../utils/paginateData';

export default function ProductTable() {
  const { products } = useContext(AppContext);
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState('');
  const [debouncedSearch, setDebouncedSearch] = useState('');
  const [sortField, setSortField] = useState('id');
  const [sortOrder, setSortOrder] = useState('asc');
  const [columns, setColumns] = useState([
    'id',
    'image',
    'name',
    'category',
    'price',
    'stock',
    'status',
    'actions',
  ]);

  const dragColIndex = useRef(null);
  const itemsPerPage = 10;

  useEffect(() => {
    const handler = setTimeout(() => setDebouncedSearch(search), 300);
    return () => clearTimeout(handler);
  }, [search]);

  const filteredProducts = useMemo(() => {
    return products.filter((p) => p.name.toLowerCase().includes(debouncedSearch.toLowerCase()));
  }, [products, debouncedSearch]);

  const sortedProducts = useMemo(() => {
    return [...filteredProducts].sort((a, b) => {
      if (a[sortField] < b[sortField]) return sortOrder === 'asc' ? -1 : 1;
      if (a[sortField] > b[sortField]) return sortOrder === 'asc' ? 1 : -1;
      return 0;
    });
  }, [filteredProducts, sortField, sortOrder]);

  const paginatedProducts = useMemo(
    () => paginateData(sortedProducts, currentPage, itemsPerPage),
    [sortedProducts, currentPage, itemsPerPage],
  );

  const totalPages = Math.ceil(sortedProducts.length / itemsPerPage);

  const toggleSort = useCallback(
    (field) => {
      if (field === 'image' || field === 'actions') return;
      setSortOrder((prev) => (sortField === field ? (prev === 'asc' ? 'desc' : 'asc') : 'asc'));
      setSortField(field);
    },
    [sortField],
  );

  const handleDrop = useCallback((fromIndex, toIndex) => {
    if (fromIndex === toIndex) return;
    setColumns((prevCols) => {
      const updated = [...prevCols];
      const [moved] = updated.splice(fromIndex, 1);
      updated.splice(toIndex, 0, moved);
      return updated;
    });
  }, []);

  const handleDragStart = useCallback((index) => {
    dragColIndex.current = index;
  }, []);

  console

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Product Table</h1>

      <input
        type="text"
        placeholder="Search by name..."
        value={search}
        onChange={(e) => {
          setSearch(e.target.value);
          setCurrentPage(1);
        }}
        className="border px-3 py-1 rounded mb-4"
      />

      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            {columns.map((col, index) => (
              <th
                draggable
                key={col}
                className="border border-gray-300 p-2 cursor-pointer"
                onClick={() => toggleSort(col)}
                onDragStart={() => handleDragStart(index)}
                onDragOver={(e) => e.preventDefault()}
                onDrop={() => handleDrop(dragColIndex.current, index)}
              >
                {col.toUpperCase()} {sortField === col ? (sortOrder === 'asc' ? '↑' : '↓') : ''}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {paginatedProducts.map((p) => (
            <tr key={p.id} className="hover:bg-gray-50 transition-all">
              {columns.map((col) => (
                <td key={col} className="border p-2">
                  {col === 'image' ? (
                    <img src={p.image} alt={p.name} className="w-12 h-12 object-cover rounded" />
                  ) : col === 'actions' ? (
                    <div className="space-x-2">
                      <button className="text-blue-500" onClick={() => alert(`Edit ${p.name}`)}>
                        Edit
                      </button>
                      <button className="text-red-500" onClick={() => alert(`Delete ${p.name}`)}>
                        Delete
                      </button>
                      <button className="text-gray-500" onClick={() => alert(`View ${p.name}`)}>
                        View
                      </button>
                    </div>
                  ) : col === 'price' ? (
                    <span>₹{p[col]}</span>
                  ) : (
                    p[col]
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      <div className="flex justify-center items-center mt-4 gap-2">
        <button
          onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
          disabled={currentPage === 1}
          className="px-3 py-1 border rounded disabled:opacity-50"
        >
          Prev
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))}
          disabled={currentPage === totalPages}
          className="px-3 py-1 border rounded disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
}
