import React, { useContext, useState, useRef } from 'react';
import { AppContext } from '../context/AppContext';
import { paginateData } from '../utils/paginateData';

export default function ProductTable() {
  const { products } = useContext(AppContext);
  const [currentPage, setCurrentPage] = useState(1);
  const [search, setSearch] = useState('');
  const [sortField, setSortField] = useState('id');
  const [sortOrder, setSortOrder] = useState('asc');
  const [columns, setColumns] = useState([
    'id',
    'image',
    'name',
    'category',
    'price',
    'stock',
    'status',
    'actions',
  ]);

  const dragColIndex = useRef(null);
  const itemsPerPage = 10;

  const filteredProducts = products.filter((p) =>
    p.name.toLowerCase().includes(search.toLowerCase()),
  );

  const sortedProducts = [...filteredProducts].sort((a, b) => {
    if (a[sortField] < b[sortField]) return sortOrder === 'asc' ? -1 : 1;
    if (a[sortField] > b[sortField]) return sortOrder === 'asc' ? 1 : -1;
    return 0;
  });

  const paginatedProducts = paginateData(sortedProducts, currentPage, itemsPerPage);
  const totalPages = Math.ceil(sortedProducts.length / itemsPerPage);

  const toggleSort = (field) => {
    if (sortField === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortOrder('asc');
    }
  };

    const handleDrop = (fromIndex, toIndex) => {

    const updated = [...columns];
    const [moved] = updated.splice(fromIndex, 1);
    updated.splice(toIndex, 0, moved);
    setColumns(updated);
  };

  const handleDragStart = (index) => {
    dragColIndex.current = index;
  };

  return (
    <div className="p-4">
      <h1 className="text-2xl font-bold mb-4">Admin Product Table</h1>

      {/* Search */}
      <input
        type="text"
        placeholder="Search by name..."
        value={search}
        onChange={(e) => {
          setSearch(e.target.value);
          setCurrentPage(1);
        }}
        className="border px-3 py-1 rounded mb-4"
      />

      {/* Table */}
      <table className="w-full border-collapse border border-gray-300">
        <thead>
          <tr className="bg-gray-200">
            {columns.map((col, index) => (
              <th
                draggable
                key={col}
                className="border border-gray-300 p-2 cursor-pointer"
                onClick={() => col !== 'image' && col !== 'actions' && toggleSort(col)}
                onDragStart={() => handleDragStart(index)}
                onDragOver={(e) => e.preventDefault()}
                onDrop={() => handleDrop(index)}
              >
                {col.toUpperCase()} {sortField === col ? (sortOrder === 'asc' ? '↑' : '↓') : ''}
              </th>
            ))}
          </tr>
        </thead>
        <tbody>
          {/* {paginatedProducts.map((p) => (
            <tr key={p.id} className="hover:bg-gray-50">
              <td className="border p-2">{p.id}</td>
              <td className="border p-2">
                <img src={p.image} alt={p.name} className="w-12 h-12 object-cover rounded" />
              </td>
              <td className="border p-2">{p.name}</td>
              <td className="border p-2">{p.category}</td>
              <td className="border p-2">${p.price}</td>
              <td className="border p-2">{p.stock}</td>
              <td
                className={`border p-2 ${
                  p.status === 'In Stock' ? 'text-green-600' : 'text-red-600'
                }`}
              >
                {p.status}
              </td>
              <td className="border p-2 space-x-2">
                <button className="text-blue-500">Edit</button>
                <button className="text-red-500">Delete</button>
                <button className="text-gray-500">View</button>
              </td>
            </tr>
          ))} */}
          {paginatedProducts.map((p) => (
            <tr key={p.id} className="hover:bg-gray-50 transition-all">
              {columns.map((col) => (
                <td key={col} className="border p-2">
                  {col === 'image' ? (
                    <img src={p.image} alt={p.name} className="w-12 h-12 object-cover rounded" />
                  ) : col === 'actions' ? (
                    <div className="space-x-2">
                      <button className="text-blue-500">Edit</button>
                      <button className="text-red-500">Delete</button>
                      <button className="text-gray-500">View</button>
                    </div>
                  ) : (
                    p[col]
                  )}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>

      {/* Pagination */}
      <div className="flex justify-center items-center mt-4 gap-2">
        <button
          onClick={() => setCurrentPage((p) => Math.max(p - 1, 1))}
          disabled={currentPage === 1}
          className="px-3 py-1 border rounded disabled:opacity-50"
        >
          Prev
        </button>
        <span>
          Page {currentPage} of {totalPages}
        </span>
        <button
          onClick={() => setCurrentPage((p) => Math.min(p + 1, totalPages))}
          disabled={currentPage === totalPages}
          className="px-3 py-1 border rounded disabled:opacity-50"
        >
          Next
        </button>
      </div>
    </div>
  );
}
